package week1.day1;

public class Factorial {

	public static void main(String[] args) {
		
		
		int number=5;
		int i,fact=1;
		for(i=1;i<=number;i++)
		{	
			fact=fact*i;
			
		}
		System.out.println("factorial of "  + number+  " is:"+fact);
		// TODO Auto-generated method stub

	}

}
